import React from 'react';
import { Card, CardImg,Breadcrumb, BreadcrumbItem,Media,CardText,CardBody } from 'reactstrap';
import { Link } from 'react-router-dom';

function RenderDish({dish}){
    return(
    <Card>
      <CardImg top src={dish.image} alt={dish.name} />
      <CardBody>
        <CardTitle>{dish.name}</CardTitle>
        <CardText>{dish.description}</CardText>
      </CardBody>
    </Card>);
}
function renderComment({dish}) {
    if (dish != null && dish.comments != null) {
      const fetch = dish.comments.map(list => {
        return (

          <div key={list.id}>

            <p>{list.comment}</p>
            <p>---{list.author},<span> {this.formatter.format(Date.parse(list.date))}</span></p>
          </div>)
      }
      )
      return (

        <div>

          <h4>Comments</h4>

          <ul class="list-unstyled">

            {fetch}

          </ul>

        </div>

      )
    }
    else {
      return (
        <div></div>
      )
    }
  };



export default DishDetail;
