import React, { Component } from 'react'
import {
  Card, CardImg, CardText, CardBody,
  CardTitle
} from 'reactstrap';;


class dishDetail extends Component {
  constructor(props) {
    super(props);
    this.state = {
    };
  }

  formatter = new Intl.DateTimeFormat("en-GB", {
    year: "numeric",
    month: "long",
    day: "2-digit"
  });

  renderDish(dish) {
    if (dish != null)
      return (
        <Card>
          <CardImg top src={dish.image} alt={dish.name} />
          <CardBody>
            <CardTitle>{dish.name}</CardTitle>
            <CardText>{dish.description}</CardText>
          </CardBody>
        </Card>
      );
    else
      return (
        <div></div>
      );
  }
  renderComment(dish) {
    if (dish != null && dish.comments != null) {
      const fetch = dish.comments.map(list => {
        return (

          <div key={list.id}>

            <p>{list.comment}</p>
            <p>---{list.author},<span> {this.formatter.format(Date.parse(list.date))}</span></p>
          </div>)
      }
      )
      return (

        <div>

          <h4>Comments</h4>

          <ul class="list-unstyled">

            {fetch}

          </ul>

        </div>

      )
    }
    else {
      return (
        <div></div>
      )
    }
  };



  render() {
    return (
      <div className="row">
        <div className="col-12 col-md-5 m-1 ml-3">
          {this.renderDish(this.props.selectedDish)}
        </div>
        <div className="col-12 col-md-5 m-1">
          {this.renderComment(this.props.selectedDish)}
        </div>
      </div>

    );

  }



}
export default dishDetail;
