import React from 'react';
//import { Media } from 'reactstrap';CardText, CardBody,

import DishDetail from '/home/knox/react/test/src/components/DishdetailComponent'
import { Card, CardImg, CardImgOverlay, NavbarBrand, Navbar, CardTitle } from 'reactstrap';
import { DISHES } from '../shared/dishes';
import Menu from './MenuComponent';


class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      dishes: DISHES,
      selectedDish: null
    }
  }

  onDishSelect(dishId) {
    this.setState({ selectedDish: dishId });
  }

 render(){


    return (
      <div>
        <Navbar dark color="primary">
          <div className="container">
            <NavbarBrand href="/">Ristorante Con Fusion</NavbarBrand>
          </div>
        </Navbar>
        <Menu dishes={this.state.dishes}  onClick={(dishId)=>this.onDishSelect(dishId)}    />


        <DishDetail selectedDish={this.state.selectedDish} />

      </div>
    )
  }
}


export default Main;
