import menu from  "./MenuComponents.js" 
import React, { Component } from 'react';


class DishDetail extends Component{
    constructor(props){
       super();
       this.state = {
           selectedDish: null
       }
    }
    onDish(dish){
        this.setState({selectedDish:dish})
    }
    renderComment(dish){
        if (dish != null)
        return(
            <div>
                <h4>Comments</h4><br />
                {dish.comment["comment"]}

            </div>
        );
    else
        return(
            <div></div>
        );
    }
} 
export default DishDetail
