import React, { Component } from 'react'
import Main from './MainComponent.js'
import {
  Card, CardImg, CardText, CardBody,
  CardTitle
} from 'reactstrap';;





  function renderDish({dish}) {
    if (props.dish != null)
      return (
        <Card>
          <CardImg top src={props.dish.image} alt={props.dish.name} />
          <CardBody>
            <CardTitle>{props.dish.name}</CardTitle>
            <CardText>{props.dish.description}</CardText>
          </CardBody>
        </Card>
      );
    else
      return (
        <div></div>
      );
  }
 function renderComment({dish}) {
    if (props.dish != null && props.dish.comments != null) {
      const formatter = new Intl.DateTimeFormat("en-GB", {
        year: "numeric",
        month: "long",
        day: "2-digit"
      });
      const fetch = props.dish.comments.map(list => {
        return (

          <div key={list.id}>

            <p>{list.comment}</p>
            <p>---{list.author},<span> {formatter.format(Date.parse(list.date))}</span></p>
          </div>)
      }
      )
      return (

        <div>

          <h4>Comments</h4>

          <ul class="list-unstyled">

            {fetch}

          </ul>

        </div>

      )
    }
    else {
      return (
        <div></div>
      )
    }
  };



const DishDetail = (props) =>{
    return (
      <div className="container">
      <div className="row">
        <div className="col-12 col-md-5 m-1">
          <renderDish dish={props.dish} />
        </div>
        <div className="col-12 col-md-5 m-1">
           <renderComment dish={props.dish}/>
        </div>
      </div>
      </div>
    );

    }




export default DishDetail;
